@RunWith(AndroidJUnit4::class)
class AdListActivityTest {

    @get:Rule
    val mActivityRule = ActivityTestRule<AdListActivity>(AdListActivity::class.java, false, false)

    lateinit var server: MockWebServer

    @Before
    fun setup() {
        server = MockWebServer()
        server.start()
        Constants.BASE_URL = server.url("/").toString();
    }


    @Test
    fun onEmptyList_noResultHeadingIsDisplayedAndRestViewsAreHidden() {
        server.enqueue(
            MockResponse()
                .setResponseCode(200)
                .setBody(
                    "{\"results\": []}"
                )
        )
        mActivityRule.launchActivity(null)
        onView(withId(R.id.textViewNoItemsFound)).check(matches((isDisplayed())))
        onView(withId(R.id.textViewHeading)).check(matches(not(isDisplayed())))
        onView(withId(R.id.editTextSearch)).check(matches(not(isDisplayed())))
    }

